import { useState } from "react";

import logo from "./assets/investment-calculator-logo.png";
import Form from "./Components/Form";
import ColumnTables from "./Components/ColumnTables";

function App() {
  const [yearlyData, setYearlyData] = useState([]);

  const calculateHandler = (userInput) => {
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    let currentSavings = +userInput["saving"]; // feel free to change the shape of this input object!

    const yearlyContribution = +userInput["contribution"]; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput["return"] / 100;
    const duration = +userInput["durationValue"];

    // // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      //const totalInterest = yearlyInterest[i]
      let totalInterest = 0;

      const newData = {
        year: i + 1,
        yearlyInterest: +yearlyInterest.toFixed(2),
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
        totalInterest: totalInterest,
      };
      setYearlyData([newData, ...yearlyData]);

      // yearlyData.push({
      //   // feel free to change the shape of the data pushed to the array!
      //   year: i + 1,
      //   yearlyInterest: +yearlyInterest.toFixed(2),
      //   savingsEndOfYear: currentSavings,
      //   yearlyContribution: yearlyContribution,
      //   totalInterest: totalInterest,
      // });
    }

    // do something with yearlyData ...
  };
  console.log(yearlyData);
  return (
    <div>
      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}
      <Form onSubmitHandler={calculateHandler}></Form>
      <table className="result">
        <thead>
          <tr>
            <th>Year</th>
            <th>Total Savings</th>
            <th>Interest (Year)</th>
            <th>Total Interest</th>
            <th>Invested Capital</th>
          </tr>
        </thead>
        <tbody>
          {yearlyData.map((year) => {
            return <ColumnTables data={year}> </ColumnTables>;
          })}
          <tr>
            <td>YEAR NUMBER</td>
            <td>TOTAL SAVINGS END OF YEAR</td>
            <td>INTEREST GAINED IN YEAR</td>
            <td>TOTAL INTEREST GAINED</td>
            <td>TOTAL INVESTED CAPITAL</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
